		/* Calculate the prevMinusDM and prevPlusDM */
		today++;
		tempReal = dataShape[today][TA.HIGH];
		diffP = tempReal - prevHigh; /* Plus Delta */
		prevHigh = tempReal;

		tempReal = dataShape[today][TA.LOW];
		diffM = prevLow - tempReal; /* Minus Delta */
