    this.SetSettings(settings);

    function TRUE_RANGE(TH, TL, YC, OUT) {
        return '\
              ' + OUT + ' = ' + TH + '-' + TL + '; \
              tempReal2 = Math.abs(' + TH + '-' + YC + '); \
              if( tempReal2 > ' + OUT + ' ) \
                 ' + OUT + ' = tempReal2; \
              tempReal2 = Math.abs(' + TL + '-' + YC + '); \
              if( tempReal2 > ' + OUT + ' ) \
                 ' + OUT + ' = tempReal2;';
    }

    if (startIdx < 0)
        throw 'TA_OUT_OF_RANGE_START_INDEX';
    if ((endIdx < 0) || (endIdx < startIdx))
        throw 'TA_OUT_OF_RANGE_END_INDEX';
